package javawin01;

public class Day01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=0; //선언문 변수이름이 a 초기값 0
		int b=0; //선언문 변수이름이 b 초기값0
		a=30; //30을 a변수 값에 대입
		b=50; //30을 b변수 값에 대입
		int c=0; //선언문 변수이름이 c 초기값 0
		c=a+b; // a와b 더한값을c에 대입하라
		System.out.println(c); //c변수 값을 콘솔에 출력
		//System.out.println() 명령어는 콘솔에 출력하라
		// 실행하는 방법은 ctrl키를 누른 상태에서 f11번 키

	}

}
